    public class PlacesArrayControl
    {
        public IDictionary<string, PlacesFab> AllPlaces { get; private set; }
        public readonly List<string> EnigmaPosition;
        public PlacesArrayControl()
        {
            AllPlaces = new Dictionary<string, PlacesFab>()
            {
                {"Stará radnice",new PlacesFab(new Location(49.1439250927757, 15.002700293254057),"Stará radnice","Výstavní dům Stará radnice na náměstí Míru zve návštěvníky k objevování různých příběhů. Kouzlo návratu do bezstarostných dětských let zažijí návštěvníci při návštěvě modelového kolejiště nebo expozice Jindřichohradecké úzkokolejky. S využitím interaktivních prvků si mohou zejména děti užít a vyzkoušet rušný železniční provoz. Ve stálé expozici „Z dějin městské samosprávy“ nahlédnete poutavou a zábavnou formou do historie Jindřichova Hradce. Prohlídku radnice můžete završit návštěvou obřadní síně architekta Jana Kotěry. Galerie nabízí výpravu do historie i současnosti českých výtvarníků, kteří tvoří pro děti. ","Městská radnice na tomto místě stávala pravděpodobně již ve středověku, podle dochovaných pozdně gotických architektonických prvků nejpozději na počátku 16. století. Původní budova bývala užší, protože mezi radnicí a vedlejším domem č. 89 vedla ulička s krámky na prodej chleba – odtud dodnes užívané označení pro průchod „Chlebnice“.","Květen až říjen 10:00 -12:00 a 13:00 -17:00 mimo pondělí","map: Výstavní dům Stará radnice,Náměstí Míru 88/I 377 01 Jindřichův Hradec--tel: +420384390977--mail: stararadnice@jh.cz-web: www.stararadnice.jh.cz",new List<string>{"VystavniDumStaraRadnice.jpg","VystavniDumStaraRadnice1.jpg","VystavniDumStaraRadnice2.jpg","VystavniDumStaraRadnice3.jpg"  },new List<int>(){0},true) },
               // {"Náměstí Míru",new PlacesFab(new Location(49.14367076821968, 15.002794770083378),"Náměstí Míru","Náměstí Míru, jehož rozlehlá plocha se vybízí k pořádání velkého množství kulturních a společenských akcí pod širým nebem. Oblíbené jsou zde různorodé letní koncerty, slavnosti či společenská setkání. Při srpnové akci Festival pouličního umění zde můžete vidět pouliční divadelníky, hudebníky či artisty, kteří předvádějí svá vystoupení za peníze do klobouku. Náměstí žije i v podzimním období, a to například při velmi oblíbených akcích Svatováclavské slavnosti nebo Svatomartinské hodování. V zimě je prostorem pro předvánoční setkávání spojená s rozsvícením vánočního stromu, zpívání koled a na Nový rok bývá tradičně rozzářeno slavnostním ohňostrojem.","Základ městského znaku tvoří zlatá pětilistá růže pánů z Hradce, zakladatelů města a první vrchnosti. Jindřichův Hradec byl od svého počátku městem poddanským, přesto se v jeho znaku objevila královská koruna. Stalo se tak privilegiem krále Vladislava II. z roku 1483, které obohatilo městský znak o dva české Ivy a královskou iniciálu,,W\" s korunou. Toto povýšení znaku panovníkem odráželo výjimečné postavení města, o které se asloužil zejména Jindřich IV. z Hradce","","",new List<string>(){"NamestiMiru.jpg", "NamestiMiru1.jpg","NamestiMiru10.jpg","NamestiMiru2.jpg","NamestiMiru3.jpg","NamestiMiru5.jpg","NamestiMiru6.jpg","NamestiMiru9"},new List<int>(){1 }) },
                { "Dům gobelínů", new PlacesFab(new Location(49.142809034930615, 15.000773659257261), "Dům gobelínů", "Přesně v roce 1910 založila ve městě své dílny pro tkaní a restaurování tapiserií Marie Hoppe Teinitzerová. Po více než sto letech se unikátní rukodělná technika dočkala důstojného zázemí ve zrekonstruovaném objektu bývalého zámeckého pivovaru. Pro návštěvníky je zde připravena expozice Muzea Jindřichohradecka s názvem Život a dílo Marie Hoppe Teinitzerové a také tzv. živá expozice, kde zručné tkadleny nechají dospělé i dětské zájemce nahlédnout do tajů výroby a restaurování tapiserií.", "Marie Teinitzerová byla zakladatelkou dílensky tkané tapiserie v Československu. Roku 1925 se zúčastnila. Světové výstavy dekorativních umění v Paříži, kde získala Grand prix za cyklus tapisérií s názvem „Řemesla“ podle návrhu F. Kysely. Tento velký úspěch odstartoval její mezinárodní ohlas.", "", "map: Dům gobelínů, Dobrovského 202/I, 377 01 Jindřichův Hradec--tel: +420 384 370 884--mail: info@dumgobelinu.cz--web: www.dumgobelinu.cz--web: www.vkcjh.cz", new List<string>() { "DumGobelinu1.JPG", "DumGobelinu14.jpg", "DumGobelinu2.JPG", "DumGobelinu3.jpg", "DumGobelinu4.JPG", "DumGobelinu5.JPG" }, new List<int>() { }, true) },
                { "Zámecký mlýn", new PlacesFab(new Location(49.14263781226307, 15.000158782305414), "Zámecký mlýn", "Stálá expozice zaměřená na historii rybníkářství, lesnictví a voroplavby na Jindřichohradecku. K vidění jsou zde především staré exponáty, které používali jindřichohradečtí rybníkáři a lesníci ke své složité práci v dobách minulých. Nechybí stručný historický exkurz do jednotlivých oborů, doplněný dobovými fotografiemi. Své místo zde také našly významné rybářské a lesnické rody, které sloužily na jindřichohradeckém panství i po několik za sebou jdoucích generací.", "Nedílnou součástí expozice je i obnovená výstavka o Františku Křižíkovi a jeho vodní elektrárně z roku 1888, která v tomto objektu stále funguje.", "", "map: Zámecký mlýn, Dobrovského 1/I, 377 01 Jindřichův Hradec--tel: +420 777 751 916--mail: michalkotyza9@seznam.cz--web: www.spolekjhlr.estranky.cz", new List<string>() { "Zahrada_MFMOM.JPG", "ZameckyMlyn.jpg", "ZameckyMlyn1.jpg", "ZameckyMlyn3.JPG", "ZameckyMlyn4.jpg", "ZameckyMlyn5.jpg", "ZameckyMlyn6.jpg" }, new List<int>() { 2 }, true) },
                { "Státní hrad a zámek", new PlacesFab(new Location(49.14201414731166, 15.000443155506707), "Státní hrad a zámek", "Státní hrad a zámek Jindřichův Hradec se svou rozlohou se řadí na třetí místo v České republice hned za Pražský hrad a zámek v Českém Krumlově. Návštěvníci si v současnosti mohou vybrat z celkem tří prohlídkových tras. \r\nTrasa „A“ – Adamovo stavení nabízí kromě reprezentativních, tzv. Zelených pokojů také portrét Perchty z Rožmberka, slavné Bílé paní tohoto zámeckého panství. Prohlídka je zakončena v manýristickém hudebním pavilonu Rondelu s plastickou zlacenou výzdobou z 16. století. \r\n\r\nTrasa „B“ – Gotický hrad, stejně jako jednu z nejlépe dochovaných hradních kuchyní u nás - Černou kuchyni, v níž se podle pověsti vařila známá sladká kaše pro chudé. \r\n\r\nVýběrový okruh“ Výběrový okruh představuje větší část stavebního vývoje hrado-zámeckého areálu Jindřichova Hradce. Návštěvníci projdou stavbami a interiéry pozdně gotickými až vrcholně renesančními. Prohlídka začíná ve staré černé kuchyni, navštíví reprezentativní pokoje stavitelů zámku a končí v unikátní manýristické stavbě, hudebním pavilonu Rondelu. \r\n\r\nPro svou jedinečnou atmosféru je Státní hrad a zámek vyhledávaným místem pro konání významných kulturních a společenských počinů: Jindřichohradecká činohra (červenec), noční prohlídky hradem a zámkem (červenec, srpen), Opera na zámku (srpen), adventní a vánoční trhy na zámku (prosinec).", "Monumentální soubor budov hradu a zámku v Jindřichově Hradci byl v roce 1996 prohlášen národní kulturní památkou a může se pyšnit zřejmě nejkrásnějším číslem popisným ve městě nad Vajgarem: 1/I.", "Duben až říjen 10:00 - 17:00 hodin, denňě mimo pondělí", "", new List<string>() {/*"SousosiNejsvetejsiTrojice.JPG", "SousosiNejsvetejsiTrojice1.jpg", "SousosiNejsvetejsiTrojice2.jpg",*/ "StatniHradAZamektit.jpg", "StatniHradAZamek.jpg", "StatniHradAZamek1.JPG", "StatniHradAZamek2.jpg", "StatniHradAZamek3.JPG", "StatniHradAZamek4.JPG", "StatniHradAZamek5.JPG", "StatniHradAZamek6.jpg", "StatniHradAZamek7.jpg", "StatniHradAZamek8.jpg", "StatniHradAZamek9.jpg" }, new List<int>() { 3, 4, 5, 6, 7 }, true) },
               // {"Černá věž, hladomorna", new PlacesFab(new Location(49.14435976339995, 15.001896782476472),"Černá věž, hladomorna","Věž je jednou z nejstarších staveb zámeckého areálu z doby po roce 1220. Je vysoká 32 metrů, zdi jsou silné místy až 3,5 metru. Během výstupu na věž lze nahlédnout do interiéru nejstarší hradní kuchyně, objevené při nedávných opravách. Na ochoz věže, ze kterého se otevírá pohled na celé město a okolí, vede 149 schodů. Věž také sloužila jako vězení. ","Za zradu krále Albrechta Habsburského byl do věže roku 1438 vsazen Zikmund z Vartenberka, který zde zemřel hlady. Odtud plyne název pro tuto věž – Hladomorna.","","map: Státní hrad a zámek Jindřichův Hradec, Dobrovského 1/I, 377 01   Jindřichův Hradec--tel: +420384321279--mail:jindrichuvhradec@npu.cz--web: www.zamek-jindrichuvhradec.cz","MestskaVyhlidkovaVez.jpg") },
                { "Muzeum Jindřichohradecka", new PlacesFab(new Location(49.143731447352515, 15.000710028799542), "Muzeum Jindřichohradecka", "Muzeum Jindřichohradecka sídlí ve dvou budovách, a to na Balbínově náměstí a ve Štítného ulici a jeho historie se píše od roku 1882. Většina expozic je situována do objektu bývalého jezuitského semináře na Balbínově náměstí. Nejznámějším exponátem Muzea Jindřichohradecka je největší lidový mechanický betlém na světě – Krýzovy jesličky. Muzeum dále nabízí např. expozici malovaných ostrostřeleckých terčů a terčových zbraní, síň věnovanou světoznámé operní pěvkyni Emě Destinnové nebo expozici s názvem Letecká bitva nad Jindřichohradeckem, stálou výstavu prezentující výrobu šicích strojů Lada, kolekci vrcholně a pozdně gotické dřevořezby, představení historie cechů a řemesel nebo ojedinělou sbírku památek na nejvýznamnější mimopražskou tiskárnu 19. století – Landfrasovu tiskárnu.", "Muzeum Jindřichohradecka uchovává ve své sbírce přes 60 tisíc položek, což představuje více než 100 tisíc sbírkových předmětů mimořádné historické, umělecké a dokumentační hodnoty. Figurky v Krýzových jesličkách jsou vyrobeny z kašírovací hmoty nebo ze dřeva. Původně ruční pohon mechanismu jesliček je nyní nahrazen elektromotorem. ", "", "map: Muzeum Jindřichohradecka, Balbínovo náměstí 19/I, 377 01 Jindřichův Hradec--tel: +420384363660--mail: muzeum@mjh.cz--web: www.mjh.cz", new List<string>() {/*"MasarykovoNamesti1.JPG", "MasarykovoNamesti11.jpg", "MasarykovoNamesti21.jpg", "MestskyParkAMertovySady11.JPG", "MestskyParkAMertovySady21.JPG", "MestskyParkAMertovySady31.JPG", "MestskyParkAMertovySady41.JPG", "MuzeumFotografie1.JPG", "MuzeumFotografie21.jpg", "MuzeumFotografie31.jpg",*/ "MuzeumJindrichohradecka.JPG", "MuzeumJindrichohradecka1.jpg", "MuzeumJindrichohradecka11.jpg", "MuzeumJindrichohradecka21.jpg" }, new List<int>() { 7, 8 }, true) },
                { "Hvězdárna a planetárium F. Nušla", new PlacesFab(new Location(49.131407361731036, 14.999831849232624), "Hvězdárna a planetárium F. Nušla", "Hvězdárna a planetárium prof. Františka Nušla je pojmenována po významném českém astronomovi, vědci a pedagogovi. Založena byla v roce 1961 a před pár lety dostala zcela nový kabát. Přibylo supermoderní laserové digitální planetárium a špičková sestava dalekohledů v hlavní kopuli. Pro návštěvníky je připraven bohatý program, od projekce hvězdného nebe a filmů s astronomickou tématikou v planetáriu, po živé pozorování Slunce, večerní a noční oblohy dalekohledy v kopuli.", "Celoevropskou raritou je umístění hvězdárny přesně na 15. poledníku, který definuje místní čas. Další zajímavostí je malá planetka (21873)“Jindřichůvhradec“, obíhající v hlavním pásu planetek mezi Marsem a Jupiterem.", "", "", new List<string> { "HvezdarnaAPlanetarium1.jpg", "HvezdarnaAPlanetarium2.jpg", "PolednikAHvezdarna15.jpg" }) },
                { "Muzeum fotografie a moderních obrazových médií", new PlacesFab(new Location(49.14440008782829, 15.000775505897662), "Muzeum fotografie a moderních obrazových médií", "Bývalá jezuitská kolej, která byla pro účely muzea rozsáhle zrekonstruována, se stala místem pro setkávání laické i odborné veřejnosti. Ve sbírkách mají své zastoupení práce předních domácích i zahraničních autorů, ale i neznámých fotografů. Velmi oblíbené jsou především u nastupující generace fotografů tematicky zaměřené fotografické workshopy", "Při rekonstrukci objektu bývalé jezuitské koleje byla pod téměř padesáti vrstvami nátěru objevena stropní malba pitvy, šokující překvapení nejen pro restaurátora Tomáše Švédu, ale i pro pracovníky z historického ústavu a lékařské fakulty. Jezuité totiž měli zakázáno zacházet s lidským tělem, takže se ukázalo, že je zde absolutní rarita celosvětového významu.", "Květen až prosinec, denně mimo pondělí 10:00 - 12:00, 13:00 -17:00", "map: Muzeum fotografie a moderních obrazových médií, Kostelní 20/I, 377 01 Jindřichův Hradec--tel: +420384362459 (kancelář)--tel: +420384361946 (pokladna)", new List<string>() { "MuzeumFotografie1.JPG", "MuzeumFotografie21.jpg", "MuzeumFotografie31.jpg", "MuzeumJindrichohradecka.JPG", "MuzeumJindrichohradecka1.jpg", "MuzeumJindrichohradecka11.jpg", "MuzeumJindrichohradecka21.jpg" }, new List<int>() { 5 }, true) },
                { "Aqua show – sv. Florián", new PlacesFab(new Location(49.14604344800071, 15.00480338883891), "Aqua show – sv. Florián", "Unikátní světelná podívaná, v níž se prolínají světelné efekty, videoprojekce a obrazce tvořené kapkami padající vody s tóny působivé hudby. Zhlédnout ji můžete v pasáži Obchodně - medicínského centra sv. Florián. Najdete zde také stylové obchůdky, provozovny služeb a příjemné posezení v Kavárně a cukrárně Florián.", "Tato fontána je jediná svého druhu v České republice.", "Pondělí až pátek 9:00 - 19:00 hodin Sobota 9:00 - 12:00 hodin (platí pro obchodní centrum i pro fontánu) Fontána spustěna v každou celou hodinu", "map: Obchodně – medicínské centrum, Růžová 41, 377 01 Jindřichův Hradec--tel: +420774421272--mail: jana.blabolilova@rbbinvest.cz--web: www.svflorian.cz", new List<string>() { "Aquapark1.jpeg", "Aquapark11.jpeg", "Aquapark14.jpeg", "Aquapark21.jpg", "Aquapark31.jpg", "Aquapark41.jpg", "AquashowSv.Florian1.jpg", "AquashowSv.Florian11.jpg" }, new List<int>() { 9 }, true)},
                { "Kostel sv. Jana Křtitele a výstavní prostory Muzea ve Štítného", new PlacesFab(new Location(49.14443412602062, 15.004977401581947), "Kostel sv. Jana Křtitele a výstavní prostory Muzea ve Štítného", "Gotický kostel, na jehož místě stávala původně románská svatyně, vznikal postupně od začátku 13. století. V následujícím století byla postavena kaple sv. Mikuláše, jež bývá někdy označována za perlu vrcholné gotiky v jižních Čechách, a zahájena stavba kláštera. Kostel, který je ve správě jindřichohradeckého muzea, je přístupný v letních měsících. Kostel slouží jako koncertní síň, jižní boční loď kostela využívá muzeum k výstavním účelům. ", "", "Květen - žíří, denně mimo pondělí, 9:00 -12:00 a 13:00 - 16:00", "", new List<string>() { "KostelSv.JanaKrtitele11.jpg", "KostelSv.JanaKrtitele21.jpg", "KostelSv.JanaKrtitele31.jpg", "KostelSv.JanaKrtitele41.jpg", "KostelSv.JanaKrtitele51.jpg", "KostelSv.JanaKrtitele61.jpg" }, new List<int>() { }, true) },
                { "Fotbalgolf", new PlacesFab(new Location(49.136406722320515, 15.032603887599036), "Fotbalgolf", "Fotbalgolf se hraje na 18 drahách o celkové délce přes 1,1 kilometru na ploše zhruba tří hektarů. Nejen tenhle zábavný a originální sport je chloubou jindřichohradeckého SportParku, který naleznete na okraji města. Kromě fotbalgolfu mysleli jeho majitelé i na bikery, pro které vytvořili dvě různé tratě. Na své si ve SportParku přijdou jak děti, na které čeká dětský trail s velkým množstvím klopenek, boulí a lavic, tak i zkušenější bikeři, kteří si můžou otestovat své jezdecké dovednosti a zaskákat na profi trati. Nedílnou součástí areálu je také kryté ohniště s posezením, dětské hřiště, pláž, terasa a občerstvení. ", "", "", "map: SportPark R&F s.r.o, poblíž č.p. 185 Otín, 377 01  Jindřichův Hradec--tel: +420724539396--mail: sportparkraf@gmail.com--web: www.sportparkraf.cz", "Fotbalgolf.jpg") },
              //  {"Cesta zahradou",new PlacesFab(new Location(49.14855163102118, 14.999744820220277),"Cesta zahradou", "Po pravé straně od brány vidíte vchod do zahrady pod Adamovým stavením, které je typickým příkladem renesanční stavby 16. století. Stavba byla zahájena v 60. letech 16. století za Jáchyma a Adama z Hradce a dokončena v 90. letech 16. století za Adama II. z Hradce. Tento renesanční zámek zvaný dříve „Nové stavení\" byl vystavěn podle projektů italských stavitelů Antonína Vlacha a později Baldassara Maggiho z Arogna. \n U tak velkého komplexu byste možná čekali i velkou zahradu.Původní hrad byl totiž vystavěn na skalnatém ostrohu, ve výhodné poloze nad řekou Nežárkou a Hamerským potokem.Tato poloha měla mimo jiné usnadnit obranu při vpádu nepřítele.A právě poloha je příčinou nedostatku místa pro umístění velké zahrady, jak jsme tomu zvyklí u jiných zámků.", "Zámecká zahrada již několikrát posloužila jako místo pro uskutečnění tzv. fair trade Férové snídaně, kdy se ve stejný den a ve stejný čas na více než stovce míst po celé ČR sejdou tisíce lidí, aby daly najevo, že podporují fairtradové a lokální pěstitele. Stává se tak na Světový den pro fair trade, který se slaví každoročně druhou květnovou sobotu.", "","",new List<string>(){"CestaZahradou1.jpg", "CestaZahradou11.JPG", "CestaZahradou21.JPG", "CestaZahradou31.JPG", "CestaZahradou41.jpg"},new List < int >() { },false)},
               // {"Jezuitska kolej s kaplí sv. Maři Magdaleny",new PlacesFab(new Location(49.14394017501936, 15.001261398268566), "Kaple sv. Maří Magdaleny stojí na místě nejstaršího jindřichohradeckého kostela, ke kterému byla později přistavěna jezuitská kolej. Kaple sv. Maří Magdaleny je jedním ze tří nejstarších barokních kostelů v Čechách. Barokně působí její vzdušný a světlý interiér i přesto, že je tohoto dojmu dosaženo místy ještě renesančními výtvarnými prostředky a v presbytáři jsou ponechána původní gotická okna.\n\nKostel je připomínán již ve 13. století. Roku 1615 vyhořel, byl opraven, v roce 1778 byl odsvěcen a spolu s kolejí začal o rok později sloužit armádě. Přilehlá jezuitská kolej je pozdně renesanční stavba z let 1595-1605.\n\nKaple je nyní využívána k výstavním, kulturním a společenským účelům. Pravidelně se zde pořádají koncerty v rámci slavností Adama Michny z Otradovic, různá slavnostní předávání a vzhledem k tomu, že se jedná o odsvěcenou kapli, jsou zde velmi oblíbené i civilní svatební obřady.", "Pro zahájení výuky na jezuiském gymnáziu musela nejprve vrchnost vykoupit a nechat zbourat více než dvacet měšťanských domů při Nežárecké bráně. Velkolepé výstavbě renesanční budovy jezuitské koleje padla za oběť dokonce i fara a starý špitál s kostelem sv. Maří Magdaleny. Při jihovýchodním nároží koleje tam místo něho vyrostla nejcennější z jindřichohradeckých jezuitských staveb, raně barokní kaple sv. Maří Magdaleny. Na přelomu dvacátých a třicátých let 17. století ji nechal vystavět Vílém Slavata a zvolil si ji za svou monumentální hrobku.","", "","",new List<string>(){"JezuitskaKolejSKapliSv.MariMagdaleny.JPG", "JezuitskaKolejSKapliSv.MariMagdaleny1.jpg", "JezuitskaKolejSKapliSv.MariMagdaleny2.jpg", "JezuitskaKolejSKapliSv.MariMagdaleny3.jpg", "JezuitskaKolejSKapliSv.MariMagdaleny4.jpg"},new List < int >() { },false)},
               // {"Pohled z Karlova",new PlacesFab(new Location(49.1392023895223, 14.998676687159094), "Pohled z Karlova", "Pohled na zámecký areál s rondelem z Karlova je dechberoucí. Na obou březích Nežárky stály koželužské domy s mansardovými střechami, v nichž se sušívaly kůže, patřící místním koželuhům. V polovině 16. století jich mezi měšťany bylo přes dvacet. Svým počtem je ovšem několikanásobně převyšovali soukeníci, kteří si obvykle u vrchnosti pronajímali četné valchy a bělidla na březích Nežárky. Několik valch bylo později zrušeno a na jejich místě pak vznikla například papírna či šindelna. Španělské křídlo zámku slouží i dnes k reprezentativním účelům.V Rytířském sále Španělského křídla, který se táhne přes celou délku budovy, se konají koncerty, divadelní představení, plesy, pronajmout si ho můžete i k soukromým účelům.Koná se zde například závěrečný koncert jihočeského festivalu Concertino Praga, své skladby zde každoročně představuje i Jindřichohradecký symfonický orchestr.", "Velkolepou přestavbu rodové rezidence pánů z Hradce završilo vybudování zahradního altánu v jihozápadním cípu ostrohu. Adam II. tu patrně podle projektu Baltazara Maggiho nechal v letech 1591-1596 místo původní okrouhlé bašty vystavět skutečný skvost evropského stavitelství 16. století - renesanční Rondel. Práce se ujal architekt Giovanni Maria Facconi s kameníkem Antoniem Comettou. Zahradní altán, obdoba dobově populárních letohrádků, sloužil majitelům zámku nejen k reprezentaci, ale i k zábavě.", "","",new List<string>(){"PohledZKarlovatit.JPG", "PohledZKarlova.JPG", "PohledZKarlova1.JPG", "PohledZKarlova2.jpg", "PohledZKarlova3.JPG", "PohledZKarlova5.JPG", "PohledZKarlova6.jpg"},new List < int >() { },false)},
               // {"Parčík na 15. poledníku",new PlacesFab(new Location(49.1446490459089, 15.001941570965526), "Parčík na 15. poledníku", "Po překročení 15. poledníku si udělejte malou přestávku v malebném zákoutí Muzea fotografie a moderních obrazových médií. Původní parkoviště bylo přestavěno na příjemný parčík a odpočinkovou zónu přímo v centru města. Místo bývá občas využíváno jako prostor pro pořádání různých kulturních a společenských akcí.", "","","",new List<string>(){"ParcikNa15.Poledniku.jpg" , "Polednik151.jpg", "Polednik152.JPG"},new List < int >() { },false)},
               // {"Husovy sady s vyhlidkou",new PlacesFab(new Location(49.14561192438127, 15.001327161898331),"Husovy sady s vyhlidkou", "Svůj název dostaly sady podle sochy mistra Jana Husa, která ozdobila část parku nad údolím řeky Nežárky v roce 1923. V roce 1997 se sešlo několik našich i zahraničních umělců na prvním ročníku Mezinárodního žulového sympozia. Po několik let pak jejich následovníci dostávali příležitost k zajímavé sochařské tvorbě. Pro plastiky město vyčlenilo právě příjemné prostředí městských parků. Kamenná díla autorů z celého světa se tak stala přirozenými partnery staletých stromů.\r\nPo pár metrech se vám naskýtá pohled hned na dvě jindřichohradecké dominanty, řeku Nežárku a zámecký komplex.Podobně jako rybník Vajgar je také řeka Nežárka po staletí neodmyslitelně spjata s životem jindřichohradeckých obyvatel.\r\nKoruny stromů jsou ideálním útočištěm pro trávení volného času v horkých letních dnech.Již několikrát se osvědčily během konání různých akcí, jako je Město dětem, veselé rozloučení s prázdninami pro děti a jejich rodiče.Horní část Husových sadů se dočkala výrazné revitalizace a vytvoření nové relaxační části s vodotrysky.", "Vody Nežárky poskytovaly možnost rybolovu, který si ovšem bylo třeba pronajmout od hradecké vrchnosti, a proto mezi nežárecké rybáře\" dlouho patřili kromě panských úředníků pouze jezuité a hradecký probošt. Nežárecká voda byla však především důležitým zdrojem energie, zejména pro četné mlýny, i podstatným pracovním prostředkem, jehož využívali hlavně koželuzi. Byly na nich závislé také valchy, bělidla, hamr a papírna. Středověký Hradec si zkrátka při Nežárce vybudoval svou hospodářskou zónu.", "","",new List<string>(){"HusovySadySVyhlidkou1.jpg", "HusovySadySVyhlidkou2.JPG", "HusovySadySVyhlidkou3.JPG", "HusovySadySVyhlidkou4.JPG", "HusovySadySVyhlidkou5.JPG"},new List < int >() { },false)},
               // {"Dum u Bradace",new PlacesFab(new Location(49.14310948443868, 14.998641764225496),"Dum u Bradace", "Předměstský patrový nárožní dům se třemi okny do Nežárecké ulice. Toto průčelí má trojúhelníkový štít s polovalbou a dvěma menšími okny. Je hladké, členěné pouze pásovými šambránami oken a dveří, jeden ze vstupů je zaklenut. Nároží je bosované v omítce. Ve výšce kordonové římsy (ta je pouze v bočním průčelí) je vsazena plastika hlavy, která dala domu jméno. Se jménem se ale nedochovalo spolehlivé povědomí o tom, jestli hlava označovala stav vody při povodni a nebo holičství. Boční fasáda je kromě pásové římsy zcela hladká s nepravidelně rozmístěnými otvory oken a dveří. Celková hmota domu ukazuje na starobylý goticko-renesanční původ patrně ze 16. století. Vnitřní dispozice a fragmenty historických konstrukcí mají dokumentační hodnotu. V minulém století byly údajně nalezeny i fragmenty sgrafita. Dům je spolu se svými sousedy cenný hmotou a štíty.", "Zatímco jedni hlavu ve výklenku vykládají jako symbol „bradýřství\", středověkého holičství, jiní ji považují za označení výšky vody při povodni. Např. v červenci 1839 po protržení hráze rybníka u Žirovnice „voda vystoupila až k bradáči\". Obdobně o rok později povolil rybník v Radouňce a voda zničila most v Podklášteří a sesula půl domu č. 105.", "","","DumUBradace.jpg",new List < int >() { },false)},
               // { "Nežárecká brána",new PlacesFab(new Location(49.14372075152734, 15.000043207888856), "Nežárecká brána", "Nežárecká brána je, podobně jako pozdější přilehlý bývalý jezuitský seminář, prvkem městského opevnění. Z původních tří městských bran se zachovala právě jen tato jediná, dříve též nazývaná Linecká. Původní středověká brána o dvou gotických patrech byla rozšířena při výstavbě přilehlé budovy semináře o další patro pozdně renesanční podoby s klíčovými střílnami a sedlovou střechou. Na počátku 19. století vznikl ještě nástavec trojúhelníkového štítku, do něhož byly umístěny ciferníky hodin. Pro zbourání dvou ze tří městských bran (Novoměstská a Rybnická) se rozhodli tehdejší radní s ohledem na katastrofální následky ničivého požáru v roce 1801. Zároveň došlo k zasypání hradebního příkopu mezi Novoměstským náměstím a údolím řeky Nežárky. Zbytky hradeb tak dnes připomíná již jen okrouhlá bašta v Husových sadech, která byla později klasicistně upravena, a také název Hradební ulice. Domy, které tu stojí, mají svá průčelí situována směrem od bývalých hradeb do středu města a nikoliv do parku.", "V roce 1879 byly v bráně instalovány věžní hodiny jindřichohradeckého hodináře Martina Rezka, proslulého hodinářského mistra, jehož mladší syn se stal později „hodinářem železniční tratě Veselí nad Lužnicí - Jihlava - Jindřichův Hradec - Nová Bystřice\", kdy získal výhradní právo na dodávání hodin pro nádražní budovy nově budované železnice. Brána je na západní straně ozdobena kamenným reliéfem městského znaku v renesanční kartuši, vnitřní strana brány byla na konci 17. století opatřena malovanými erby Jana Jiřího Jáchyma Slavaty a jeho manželky Marie Markéty z Rappachu.", "","",new List<string>(){"NezareckaBrana2.JPG", "NezareckaBrana3.JPG", "NezareckaBrana4.JPG"},new List < int >() { },false)},
               /* {"Proboštský kostel Nanebevzetí Panny Marie a Městská vyhlídková věž",new PlacesFab(new Location(49.14435886845946, 15.001876303581804), "Proboštský kostel Nanebevzetí Panny Marie a Městská vyhlídková věž", "=Původně gotický kostel byl postaven ve 2. polovině 14. století. Při velkém požáru se zachránila pouze kamenná křtitelnice a je z r. 1565. Kostel Nanebevzetí Panny Marie je gotické síťové trojlodí s šestibokými pilíři a chórem, které tvoří jednotný slohový celek. Přiléhající věž u kostela Nanebevzetí Panny Marie je nejvyšší věží města, měří celkem 68,3 metru, z toho báň téměř 24 metrů. Věž pochází patrně ze 14. století. V letních měsících je věž, z níž je překrásná vyhlídka na město i okolí, přístupná veřejnosti. Pravidelně v měsíci květnu musí účastníci tradiční soutěže „Běž na věž\" zdolat 153 schodů, aby se jim z ochozu věže naskytl nádherný pohled na střechy hradeckých domů.", "Farní kostel byl vystavěn okolo třetí čtvrtiny 14. století a po necelé stovce let už musel být opravován, jelikož utrpěl nejen za husitských válek, ale také při obléhání města vojskem Jiřího z Poděbrad. Péčí Jindřicha IV. z Hradce a jeho manželky Magdaleny z Gleichenu byl poté uveden do důstojného stavu. Historické prameny uvádějí, že od založení kostela byly pro něj do konce 17. století vytvořeny dvacet čtyři oltáře, z nichž pouze patnáct zůstalo tehdy zachováno. Ve století osmnáctém k nim přibyl oltář obzvláště hodnotný, postavit ho nechala Marie Josefa Černínová, rozená Slavatová.", "","","ProbostskyKostelSMestskouVyhlidkovouVezi.jpg",new List < int >() { },false)},
                {"Masarykovo namésti",new PlacesFab(new Location(49.14606955010276, 15.003574682385368),"Masarykovo namésti", "Masarykovo náměstí se nalézá na severním okraji centra okresního města Jindřichův Hradec na cestě k vlakovému i autobusovému nádraží. Ve středověku tady stávala velká gotická Pražská brána. V létě se na Masarykově náměstí konají populární promenádní koncerty. Nejpopulárnější z novodobých staveb je budova kulturního domu, kina Střelnice a pak také fontány, krášlící střed rekonstruované plochy náměstí. V místech, kde stojí KD Střelnice, probíhal kdysi parkán, kde pořádali svá cvičení jindřichohradečtí ostrostřelci. Po nich dostal jméno pozdější hostinec Na Střelnici.", "V kulturním domě Střelnice se konal první český bál v únoru 1846, jehož se zúčastnilo více než 800 lidí v sále, který byl oproti dnešnímu poloviční. Zazněla zde i píseň Kde domov můj, jež se stala později naší hymnou. V letech 1924, 1926, 1927 zde nadšení posluchači aplaudovali operní pěvkyni Emě Destinnové, která sem zajížděla ze svého sídla ve Stráži nad Nežárkou.", "","",new List<string>(){"MasarykovoNamesti1.JPG", "MasarykovoNamesti11.jpg", "MasarykovoNamesti21.jpg"},new List < int >() { },false)},
                {"Rybnik Vajgar s ostrovem",new PlacesFab(new Location(49.14285653566234, 15.007081183723518),"Rybnik Vajgar s ostrovem", "Rybník Vajgar vznikl na místě někdejších bažin při soutoku Hamerského potoka a Nežárky. První zmínka o rybníku a mostě mezi Malým a velkým Vajgarem je z roku 1399, kdy byl na tomto místě, na konci mostu oddělující oba rybníky, založen špitál sv. Alžběty. Vajgaru se dříve říkalo Velký, Městský nebo Hradní rybník, jméno Vajgar dostal až koncem 17. století. Výměra rybníku je 49 hektarů a maximální hloubka 5 metrů.\r\nV roce 1874 vznikl ve městě nejstarší sportovní oddíl - Bruslařský klub. Bruslilo se na zamrzlé hladině rybníka Vajgar, později byla činnost spolku rozšířena také o letní sport - veslování. V roce 1891 byla ustaven Bruslařský a veslařský klub, dnes fungující jako Veslařský klub Vajgar.\r\nVšechny jindřichohradecké veslaře zastínil skifař Václav Chalupa mladší, jehož vynikající sportovní výkony ho zařadily mezi světovou skifařkou špičku. Na jeho počest bývá každý rok na konci léta na rybníku pořádána veslařská regata s názvem Chalupa cup.", "Uprostřed rybníku se nachází uměle vytvořený ostrůvek, který nechal vytvořit Jaromír Evžen Černín pro svou manželku Karolínu k jejím 40. narozeninám v letech 1858-1860. Ostrůvek byl vytvořen tak, že do míst dnešního ostrova byly po výlovu zatlučeny dubové kůly na způsob ohrady. Potom byl rybník opět napuštěn, a když zamrzl, byla do vysekané díry navezena zemina a na jaře byl ostrov osázen stromy.", "","",new List<string>(){"RybnikVajgarSStrovem1.jpg", "RybnikVajgarSStrovem2.jpg", "RybnikVajgarSStrovem3.jpg", "RybnikVajgarSStrovem4.jpg", "RybnikVajgarSStrovem5.jpg"},new List < int >() { },false)},
                {"Namésti Miru se sousoSim Nanebevzeti",new PlacesFab(new Location(49.14366067880392, 15.00279912038028),"Namésti Miru se sousoSim Nanebevzeti", "Náměstí Míru, které má tvar protáhlého lichoběžníku o stranách asi 120 x 50 metrů a nachází se v jižní části středověké zástavby. Plocha náměstí je lemována řadami starých měšťanských domů s krásnými historickými fasádami, dotvářejícími krásu tohoto náměstí. Říkávalo se mu různě: Velké, Hlavní, Dolní, jmenovalo se však také Wilsonovo, Hitlerovo nebo i Stalinovo. Původně bylo náměstí menší, ve 13. století dosahovalo asi poloviční rozlohy. Směrem k jihu, tedy k hradu pánů z Hradce, se totiž tehdy terén poměrně prudce svažoval.\r\nPřestože v jednom z domů na náměstí vypukl na samém počátku 19. století zhoubný požár, který zničil téměř celé město, zachovalo si náměstí svůj starobylý ráz.", "Jindřichohradecký kolos\", jak bývalo dříve sousoší také nazýváno, vytvořil v letech 1764-1766 dačický sochař Matouš Strachovský. Tato ojedinělá památka byla postavena s přispěním obce a zejména z finančního daru jindřichohradeckého měšťana Ondřeje Josefa Bayera, zakladatele rodu jindřichohradeckých poštmistrů a bohatého důstojníka zásobovací služby císařské armády, který se zde v polovině 18. století usadil.", "","",new List<string>(){ "NamestiMiru.jpg", "NamestiMiru1.jpg", "NamestiMiru10.jpg", "NamestiMiru2.JPG", "NamestiMiru3.JPG", "NamestiMiru5.JPG", "NamestiMiru6.jpg", "NamestiMiru9.jpg"},new List < int >() { },false)},
               {"Měšťanský dům U Zlaté Husy a Langrův dům",new PlacesFab(new Location(49.14411541409469, 15.003345847047232), "Měšťanský dům U Zlaté Husy a Langrův dům", "Na měšťanském domě U Zlaté Husy je osazena pamětní deska připomínající pobyt Karla Havlíčka Borovského a pamětní deska Adama Václava Michny z Otradovic. Dům v jádru gotický byl vicekrát přestavován a dnes je hodnotný svým průčelím a klenutými prostory. Na první pohled je patrné, že u Langrova domu jde o dva domy, které spojuje v přízemí loubí. Celé pravé průčelí je pokryto sgrafity se starozákonními výjevy. Tato sgrafita vyžadují v současné době trvalou restaurátorskou péči, při níž se jim vrací původní vzhled. Dvůr Langrova domu je obestavěn i v zadní části a celek tvoří velmi příjemný prostor.", "V hostinci U Zlaté Husy strávil noc z 16. na 17. prosince 1851 Karel Havlíček Borovský při cestě do vyhnanstvi v Brixenu. U Langrů se dříve prodávalo a do dalekého okolí rozváželo téměř vše - sůl, koření, káva, rum, ale i hřebíky a železo, ba i střelný prach a poprvé v Hradci se zde začala v 19. století prodávat převratná novinka - kostkový cukr.", "","","UZlateHusy.jpg",new List < int >() { },false)},
               */
